module.exports = {
  'props': ['pageDirection'],
  'data': function () {

    return {
      'headings': [],
      'headingCss': ''
    };

  },
  'watch': {
    'pageDirection': function () {

      this.update();

    },
    'headings': {
      'handler': function () {

        this.update();

      },
      'deep': true
    }
  },
  'methods': {
    'addHeading': function () {

      let newHeading = {
        'lank': 'h2',
        'family': 'sans-serif',
        'weight': 'normal',
        'color': 'inherit',
        'bgcolor': 'inherit',
        'align': 'left',
        'indent': '0',
        'size': '120%',
        'bdstyle': 'none',
        'bdwidth': '1px',
        'bdcolor': '#000000',
        'bdtop': true,
        'bdbottom': true,
        'bdleft': true,
        'bdright': true
      };

      this.headings.push(newHeading);

    },
    'styleHeading': function (heading) {

      const headingStyleObject = {
        fontFamily: heading.family,
        fontWeight: heading.weight,
        fontSize:   heading.size,
        color:      heading.color,
        backgroundColor: heading.bgcolor,
        textAlign: heading.align,
        textIndent: `${heading.indent}em`,
        textIndent: `${heading.indent}rem`,
      };

      // 
      if (heading.bgcolor !== 'inherit' || heading.bdstyle !== 'none') {

        headingStyleObject.padding = '0.5em';

      }

      if (heading.bdstyle !== 'none') {

        if (heading.bdtop) {

          headingStyleObject.borderTop = `${heading.bdwidth} ${heading.bdstyle} ${heading.bdcolor}`;

        }

        if (heading.bdbottom) {

          headingStyleObject.borderBottom = `${heading.bdwidth} ${heading.bdstyle} ${heading.bdcolor}`;

        }

        if (heading.bdleft) {

          headingStyleObject.borderLeft = `${heading.bdwidth} ${heading.bdstyle} ${heading.bdcolor}`;

        }

        if (heading.bdright) {

          headingStyleObject.borderRight = `${heading.bdwidth} ${heading.bdstyle} ${heading.bdcolor}`;

        }

      }

      return headingStyleObject;

    },
    'update': function () {

      const cpnt = this;
      cpnt.headingCss = '';

      cpnt.headings.forEach(heading => {

        cpnt.headingCss += `
${heading.lank} {
`;

        // 
        if (cpnt.pageDirection === 'rtl') {

          cpnt.headingCss += "  margin-top: 0;\n";

        }

        // 
        if (heading.bgcolor !== 'inherit' || heading.bdstyle !== 'none') {

          cpnt.headingCss += "  padding: 0.5em;\n";

        }

        cpnt.headingCss += `  font-family: ${heading.family};
  font-weight: ${heading.weight};
  font-size: ${heading.size};
  color:  ${heading.color};
  background-color: ${heading.bgcolor};
  text-align: ${heading.align};
  text-indent: ${heading.indent}em;
  text-indent: ${heading.indent}rem;
`;

        if (heading.bdstyle !== 'none') {

          if (heading.bdtop) {

            cpnt.headingCss += `  border-top: ${heading.bdwidth} ${heading.bdstyle} ${heading.bdcolor};\n`;

          }

          if (heading.bdbottom) {

            cpnt.headingCss += `  border-bottom: ${heading.bdwidth} ${heading.bdstyle} ${heading.bdcolor};\n`;

          }

          if (heading.bdleft) {

            cpnt.headingCss += `  border-left: ${heading.bdwidth} ${heading.bdstyle} ${heading.bdcolor};\n`;

          }

          if (heading.bdright) {
            cpnt.headingCss += `  border-right: ${heading.bdwidth} ${heading.bdstyle} ${heading.bdcolor};\n`;

          }

        }

        cpnt.headingCss += "}\n";

      });

      this.$emit('change-heading-css', cpnt.headingCss);

    },
    'saveDataToLocalStorage': function () {

      localStorage.setItem('hEditor', JSON.stringify(this.$data));

    },
    'loadDataFromLocalStorage': function () {

      const cpnt = this;
      const data = JSON.parse(localStorage.getItem('hEditor'));

      Object.keys(data).forEach(function (key) {

        cpnt[key] = data[key];

      });

    }
  }
};


